    private void btnIngresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIngresarActionPerformed
        String nombreUsuario = txtUsuario.getText();
        String contrasenia = txtContrasenia.getText();
        
        Usuario usr = controlL.validarUsuario(nombreUsuario, contrasenia);
        
        if(usr != null) {
            String rol = usr.getUnRol().getNombreRol();
            
            if(rol.equals("admin")) {
                PantallaAdmin pAdmin = new PantallaAdmin(controlL, usr);
                pAdmin.setLocationRelativeTo(null);
                pAdmin.setVisible(true);
                this.dispose();
            } else if (rol.equals("user")) {
                PantallaUser pUser = new PantallaUser(controlL, usr);
                pUser.setLocationRelativeTo(null);
                pUser.setVisible(true);
                this.dispose();
            }
        } else {
            txtMensaje.setText("Usuario o contrase√±a incorrecto");
        }
        
        
    }//GEN-LAST:event_btnIngresarActionPerformed

